frappe.listview_settings['Attendance'] = {
    onload: function(listview) {
        // Add a custom button to the Attendance list view toolbar
        listview.page.add_inner_button(__('Export Check-in Report'), function() {
            open_filter_dialog();
        });
    }
};

function open_filter_dialog() {
    // Create a dialog with employee, from date, and to date fields
    let dialog = new frappe.ui.Dialog({
        title: 'Filter for Check-in Report',
        fields: [
            {
                label: 'All Employees',
                fieldname: 'all_employees',
                fieldtype: 'Check',
                default: 0,  // Default to unchecked
                reqd: 0  // Optional
            },
            {
                label: 'Employee',
                fieldname: 'employee',
                fieldtype: 'Link',
                options: 'Employee',
                reqd: 0,  // Optional
                depends_on: 'eval:!doc.all_employees',
                mandatory_depends_on: 'eval:!doc.all_employees'
            },
            {
                fieldtype: 'Column Break'
            },
            {
                label: 'From Date',
                fieldname: 'from_date',
                fieldtype: 'Date',
                default: frappe.datetime.get_today(),
                columns: 2,
                reqd: 1  // Required field
            },
            {
                label: 'To Date',
                fieldname: 'to_date',
                fieldtype: 'Date',
                default: frappe.datetime.get_today(),
                columns: 2,
                reqd: 1  // Required field
            }
        ],
        primary_action_label: 'Generate Report',
        primary_action(values) {
            // Ensure valid input
            if (!values.from_date || !values.to_date) {
                frappe.msgprint(__('Please fill all required fields.'));
                return;
            }

            if (values.to_date < values.from_date) {
                frappe.msgprint(__('To Date cannot be earlier than From Date.'));
                return;
            }

            if (values.all_employees) {
                values.employee = '';  // Clear employee if all employees are selected
            }

            // Show a progress dialog
            let progress_dialog = frappe.msgprint({
                message: __('Generating report, please wait...'),
                indicator: 'blue',
                title: __('Processing')
            });

            // Call the server-side function to generate the report
            frappe.call({
                method: 'sil.services.checkin_report_api.get_combined_checkin_report_to_hr',
                args: {
                    employee_name: values.employee || '',
                    from_date: values.from_date,
                    to_date: values.to_date
                },
                callback: function(response) {
                    // Hide the progress dialog
                    progress_dialog.hide();

                    if (response.message && response.message.file_url) {
                        // Hide the progress dialog in case of an error
                        progress_dialog.hide();

                        const fileUrl = response.message.file_url;
                        const link = document.createElement('a');
                        link.href = fileUrl;
                        link.download = response.message.file_name || 'Checkin_Report.xlsx';
                        link.click();

                        frappe.msgprint({
                            title: __('Success'),
                            indicator: 'green',
                            message: __('Check-in report successfully downloaded.')
                        });

                    } else {
                        // Hide the progress dialog in case of an error
                        progress_dialog.hide();
                        frappe.msgprint({
                            title: __('Error'),
                            indicator: 'red',
                            message: __('Error: {0}', [response.message.error || 'Unknown error'])
                        });

                    }
                },
                error: function(error) {
                    // Hide the progress dialog in case of an error
                    progress_dialog.hide();

                    frappe.msgprint({
                        title: __('Error'),
                        indicator: 'red',
                        message: __('Server error occurred while generating the report.')
                    });
                    console.error('Server error:', error);
                }
            });

            dialog.hide();  // Hide the filter dialog
        }
    });

    dialog.show();  // Show the filter dialog
}

frappe.listview_settings['Sales Invoice'] = {
    onload: function(listview) {
