};


function open_download_dialog() {
    const dialog = new frappe.ui.Dialog({
        title: __('Download Sales Invoice Report'),
        width: '400px', // Set the width of the dialog
        height: 'auto', // Adjust the height as needed
        fields: [
            {
                fieldtype: 'Section Break',
                label: __('Manager Filters'),
            },
            {
                fieldtype: 'Link',
                fieldname: 'custom_zonal_manager',
                label: __('Zonal Manager'),
                options: 'Zonal Manager',
                reqd: 0,
                columns: 2,
                description: __('(Select the Zonal Manager for the report.)')
            },
            {
                fieldtype: 'Link',
                fieldname: 'custom_regional_manager',
                label: __('Regional Manager'),
                options: 'Regional Manager',
                reqd: 0,
                columns: 2,
                description: __('(Select the Regional Manager for the report.)')
            },
            {
                fieldtype: 'Column Break' // Start a new column
            },
            {
                fieldtype: 'Link',
                fieldname: 'custom_cluster',
                label: __('Cluster'),
                options: 'Cluster',
                reqd: 0,
                description: __('(Select the Cluster for the report.)')
            },
            {
                fieldtype: 'Link',
                fieldname: 'custom_cluster_manager',
                label: __('Cluster Manager'),
                options: 'Cluster Manager',
                reqd: 0,
                description: __('Select the Cluster Manager for the report.')
            },
            {
                fieldtype: 'Section Break',
                label: __('Customer Filter'),
            },
            {
                fieldtype: 'Column Break' // Start a new column
            },
            {
                fieldtype: 'Link',
                fieldname: 'customer_name',
                label: __('Customer Name'),
                options: 'Customer',
                reqd: 0,
                description: __('Select the Customer for the report.')
            },
            {
                fieldtype: 'Section Break',
                label: __('Date Filters'),
            },
            {
                fieldtype: 'Column Break' // Start a new column for date filters
            },
            {
                fieldtype: 'Date',
                fieldname: 'starting_posting_date',
                label: __('From Date'),
                reqd: 1,
                default: frappe.datetime.get_today(),
                description: __('Select the start date for the report.')
            },
            {
                fieldtype: 'Column Break' // End the first date column and start the second
            },
            {
                fieldtype: 'Date',
                fieldname: 'ending_posting_date',
                label: __('To Date'),
                reqd: 1,
                default: frappe.datetime.get_today(),
                description: __('Select the end date for the report.')
            }
        ],
        primary_action_label: __('Download'),
        primary_action(values) {
            // Validate dates
            if (values.starting_posting_date > values.ending_posting_date) {
                frappe.msgprint(__('Error: From Date cannot be later than To Date.'));
                return;
            }

            // Show loading dialog while the report is being generated
            const loading_dialog = new frappe.ui.Dialog({
                title: __('Generating Report'),
                width: '300px', // Set width for the loading dialog
                height: 'auto', // Adjust the height as needed
                body: __('Please wait while the report is being generated.'),
                buttons: [],
                close_on_escape: false
            });
            loading_dialog.show();

            // Call the server-side function to generate and download the report
            frappe.call({
                method: 'sil.services.sales_invoice_report_api.generate_and_download_sales_invoice_report',
                args: {
                    filters: {
                        custom_zonal_manager: values.custom_zonal_manager,
                        custom_regional_manager: values.custom_regional_manager,
                        custom_cluster: values.custom_cluster,
                        custom_cluster_manager: values.custom_cluster_manager,
                        customer_name: values.customer_name,
                        starting_posting_date: values.starting_posting_date,
                        ending_posting_date: values.ending_posting_date
                    }
                },
                callback: function(response) {
                    loading_dialog.hide(); // Hide loading dialog
                    if (response.message && response.message.file_url) {
                        // Create an invisible anchor element
                        const link = document.createElement('a');
                        link.href = response.message.file_url;
                        link.download = response.message.file_name || 'sales_invoice_report.xlsx'; // Default file name if not provided
                        link.click(); // Programmatically click the link to trigger the download
                    } else {
                        frappe.msgprint(__('Error: {0}', [response.message.error || 'Unknown error']));
                    }
                },
                error: function(err) {
                    loading_dialog.hide(); // Hide loading dialog
                    frappe.msgprint(__('An unexpected error occurred. Please try again.'));
                    console.error(err);
                }
            });
            dialog.hide(); // Close the main dialog
        }
    });

    dialog.show();
}

