frappe.ui.form.on('Sales Invoice', {
    custom_state_province: function(frm) {
        // Define a function to set the query filter
        function updateCustomClusterQuery(custom_clusters) {
            frm.set_query("custom_cluster", function() {
                return {
                    filters: custom_clusters.length > 0 
                        ? [["name", "in", custom_clusters]]
                        : ["name", "in",frm.doc.custom_state_province ]  
                };
            });
            frm.refresh_field("custom_cluster");  
        }

        if (frm.doc.custom_state_province) {
            console.log("State/Province Selected: ", frm.doc.custom_state_province);

            frappe.call({
                method: "sil.services.cluster_api.get_custom_clusters",
                args: {
                    state_province: frm.doc.custom_state_province
                },
                callback: function(r) {
                    console.log("API Response: ", r);

                    if (r.message && r.message.length > 0) {
                        var custom_clusters = r.message.map(item => item.parent);
                        console.log("Custom Clusters: ", custom_clusters);
                        updateCustomClusterQuery(custom_clusters);  
                    } else {
                        console.log("No clusters found.");
                        updateCustomClusterQuery([frm.doc.custom_state_province]);  // Clear the filter
                    }
                },
                error: function(err) {
                    console.error("API Call Error: ", err);
                    updateCustomClusterQuery([frm.doc.custom_state_province]);  // Clear the filter on error
                }
            });
        } else {
            // If no state/province is selected, clear the filter
            updateCustomClusterQuery([frm.doc.custom_state_province]);
        }
    }
});

