    }
});

function customCreateSerialNo(frm) {
    let items_series_data = {};

    // Gathering data from custom item series
    frm.doc.custom_item_series.forEach(function(item_series, index) {
        items_series_data[index] = {
            "customer": frm.doc.customer,
            "sales_order_name": frm.doc.name,
            "item_name":item_series.item_name,
            "item_code": item_series.item_code,
            "item_family": item_series.item_family,
            "quantity": item_series.quantity,
            "item_series": item_series.item_series,
            "serial_nos": item_series.serial_no || ""
        };
    });

    // Show progress dialog
    let progress_dialog = frappe.msgprint({
        message: __('Processing items...'),
        indicator: 'blue',
        title: __('Please Wait'),
        wide: true,
        buttons: []
    });

    let total_items = Object.keys(items_series_data).length;
    let processed_items = 0;

    // Iterate over item series data and call method to create serial numbers
    Object.keys(items_series_data).forEach(function(key) {
        let item_series = items_series_data[key];
        
        frappe.call({
            method: "sil.services.sales_order_api.generateSerialNo", // Replace with your method path
            args: {
                items_series: JSON.stringify({ [key]: item_series }), // Passing data as JSON
                form_doc: frm.doc.name // Send form doc if necessary
            },
            callback: function(response) {
                console.log('Response received:', response);

                // Check for success response
                if (response.message && response.message.success) {
                    // Access serial numbers using the key
                    let serial_data = response.message.serial_nos[key]; 
                    if (serial_data) {
                        let item = frm.doc.custom_item_series.find(i => i.item_code === item_series.item_code);
                        if (item) {
                            // Update the 'serial_no' field with the serial_Nos from the response
                            frappe.model.set_value(item.doctype, item.name, 'serial_no', serial_data.serial_Nos);
                            console.log('Serial Nos updated for item:', item_series.item_code, 'Serial Nos:', serial_data.serial_Nos);
                        }
                    }
                    frappe.msgprint("Serial No created successfully for item: " + item_series.item_code);
                } else {
                    frappe.msgprint("Serial No creation failed for item: " + item_series.item_code + " - " + (response.message.message || 'Unknown error'));
                }

                frm.refresh_field('custom_item_series');

                // Track processed items
                processed_items += 1;

                // Hide progress dialog when all items are processed
                if (processed_items === total_items) {
                    progress_dialog.hide();
                }
            },
            error: function(error) {
                console.error('Error processing item:', item_series.item_code, 'Error:', error);
                processed_items += 1;
                frappe.msgprint("Error processing item: " + item_series.item_code);

                if (processed_items === total_items) {
                    progress_dialog.hide();
                }
            }
        });
    });
}

