function open_filter_dialog(frm) {
    // Create a dialog with employee, from date, and to date fields
    let dialog = new frappe.ui.Dialog({
        title: 'Filter for Check-in Report',
        fields: [
            {
                label: 'Employee',
                fieldname: 'employee',
                fieldtype: 'Link',
                options: 'Employee',
                reqd: 0  // Optional
            },
            {
                label: 'From Date',
                fieldname: 'from_date',
                fieldtype: 'Date',
                default: frappe.datetime.get_today(),
                columns: 2,
                reqd: 1  // Required field
            },
             {
                fieldtype: 'Column Break' // Start a new column
            },
            {
                label: 'To Date',
                fieldname: 'to_date',
                fieldtype: 'Date',
                default: frappe.datetime.get_today(),
                columns: 2,
                reqd: 1  // Required field
            }
        ],
        primary_action_label: 'Generate Report',
        primary_action(values) {
            try {
                // Ensure valid input
                if (!values.from_date || !values.to_date) {
                    frappe.msgprint(__('Please fill all required fields.'));
                    return;
                }

                // Validate that the 'to_date' is not earlier than 'from_date'
                if (values.to_date < values.from_date) {
                    frappe.msgprint(__('To Date cannot be earlier than From Date.'));
                    return;
                }

                // Call the server-side function to generate the report
                frappe.call({
                    method: 'sil.services.checkin_report_api.get_combined_checkin_report_to_hr',
                    args: {
                        employee_name: values.employee || '', // Use empty string if no employee selected
                        from_date: values.from_date,
                        to_date: values.to_date
                    },
                    callback: function(response) {
                        if (response.message && response.message.file_url) {
                            let file_url = response.message.file_url;

                            // If the URL is relative, adjust to the full URL
                            if (file_url.startsWith('./')) {
                                file_url = window.location.origin + file_url.replace('./', '/');
                            }

                            // Trigger file download
                            let link = document.createElement('a');
                            link.href = file_url;
                            link.download = 'Check_In_Report.xls';  // Set the download filename
                            link.click();

                            frappe.msgprint({
                                title: __('Success'),
                                indicator: 'green',
                                message: __('Check-in report generated and downloaded.')
                            });
                        } else {
                            frappe.msgprint({
                                title: __('Error'),
                                indicator: 'red',
                                message: __('An error occurred while generating the report.')
                            });
                            console.error('Error in generating the report:', response.message || 'Unknown error');
                        }
                    },
                    error: function(error) {
                        frappe.msgprint({
                            title: __('Error'),
                            indicator: 'red',
                            message: __('Server error occurred while generating the report.')
                        });
                        console.error('Server error:', error);
                    }
                });

                dialog.hide();
            } catch (e) {
                frappe.msgprint({
                    title: __('Error'),
                    indicator: 'red',
                    message: __('An unexpected error occurred.')
                });
                console.error('Unexpected error:', e);
            }
        }
    });

    dialog.show();
}

