        if (row.reference_type === 'Sales Invoice' || row.reference_type === 'Sales Order') {
            if (row.customer && row.reference_type && row.reference_name && row.outstanding_amount) {
                update_parent_field(frm, cdt, cdn);
            }
        } else if (row.reference_type === 'Slip No' || row.reference_type === 'Advance') {
            if (row.customer && row.reference_type) {
                update_parent_field(frm, cdt, cdn);
            }
        }
    }
});

function checkAndCallServer(frm, cdt, cdn) {
    let row = locals[cdt][cdn];
    let amount = frm.doc.amount || 0;

    if (amount > 0) {
        if (row.customer && row.reference_type && !row.reference_name) {
            frappe.call({
                method: 'sil.services.receipt_information_api.get_all_receipt_info_by_reference_type_and_cust_name',
                args: {
                    customer: row.customer,
                    reference_type: row.reference_type
                },
                callback: function(r) {
                    if (r.message) {
                        // Ensure r.message contains the expected structure
                        if (Array.isArray(r.message.reference_name)) {
                            const referenceNames = r.message.reference_name.map(item => item.name);
                            frm.fields_dict["receipt_entry"].grid.grid_rows_by_docname[cdn]
                                .get_field('reference_name').df.options = referenceNames;
                            frm.fields_dict["receipt_entry"].grid.grid_rows_by_docname[cdn]
                                .refresh_field('reference_name');
                        }
                        frappe.model.set_value(cdt, cdn, 'outstanding_amount', r.message.outstanding_amount);
                        frappe.model.set_value(cdt, cdn, 'allocated_amount', r.message.allocated_amount);
                    }
                },
                error: function(err) {
                    console.error("Error fetching receipt information: ", err); 
                    frappe.msgprint(__('An error occurred while fetching receipt information.'));
                }
            });
        } else if (row.customer && row.reference_type && row.reference_name) {
            frappe.call({
                method: 'sil.services.receipt_information_api.get_all_receipt_info_by_reference_name',
                args: {
                    customer: row.customer,
                    reference_type: row.reference_type,
                    reference_name: row.reference_name
                },
                callback: function(r) {
                    if (r.message) {
                        frappe.model.set_value(cdt, cdn, 'outstanding_amount', r.message.outstanding_amount);
                        frappe.model.set_value(cdt, cdn, 'allocated_amount', r.message.allocated_amount);
                    }
                },
                error: function(err) {
                    console.error("Error fetching receipt information: ", err); 
                    frappe.msgprint(__('An error occurred while fetching receipt information.'));
                }
            });
        }
    } else {
        frappe.msgprint(__('Please enter amount.'));
    }
}

function update_parent_field(frm, cdt, cdn) {
    let total_allocated_amount = 0.0;
    let is_valid = true;

    frm.doc.receipt_entry.forEach(function(row) {
        if (row.reference_type === 'Sales Invoice' || row.reference_type === 'Sales Order') {
            if (parseFloat(row.allocated_amount || 0.0) > parseFloat(row.outstanding_amount || 0.0)) {
                frappe.msgprint(__('The allocated amount ({0}) cannot exceed the outstanding amount ({1}).',
                    [parseFloat(row.allocated_amount || 0.0), parseFloat(row.outstanding_amount || 0.0)]));

                // Reset allocated amount if it exceeds outstanding amount
                frappe.model.set_value(cdt, cdn, 'allocated_amount', parseFloat(row.outstanding_amount || 0.0));

                is_valid = false;
                frappe.validated = false;
                return false; // Exit loop early if validation fails
            }
        }

        total_allocated_amount += parseFloat(row.allocated_amount || 0.0);
    });

    if (!is_valid) return;

    if (total_allocated_amount > 0) {
        let unallocated_amount = parseFloat(frm.doc.amount) - total_allocated_amount;
        frm.set_value('unallocated_amount', unallocated_amount);
    }

    if (total_allocated_amount !== frm.doc.amount) {
        frappe.msgprint(__('The sum of allocated amounts ({0}) must match the total amount ({1}).', 
            [total_allocated_amount, frm.doc.amount]));
        frappe.validated = false;
    } else {
        frappe.validated = true;
    }
}

