        const date = d.get_value('date') || '';
        resetSelections();
        if (executive && !d.get_value('all')) {
            fetch_receipt_numbers(frm, executive, d, selectedReceipts, amount, date);
        } else {
            resetSelections();
        }
    }

    function handleAmountChange() {
        const executive = d.get_value('executive');
        const amount = d.get_value('amount');
        const date = d.get_value('date');
        resetSelections();
        if (executive && !d.get_value('all') && amount) {
            fetch_receipt_numbers(frm, executive, d, selectedReceipts, amount, date);
        } else {
            resetSelections();
        }
    }

    function resetSelections() {
        selectedReceipts = [];
        d.set_value('receipt_number', '');
    }

    d.show();
}

function fetch_receipt_numbers(frm, executive, dialog, selectedReceipts, amount, date) {
    frappe.call({
        method: 'sil.services.receipt_information_api.getAllReceiptInfoDetailsByExecutive',
        args: { executive:executive, amount:amount, date:date },
        callback: function(r) {
            if (r.message) {
                updateDialogOptions(r, dialog, selectedReceipts, frm);
            } else {
                frappe.msgprint(__('No receipts found for the selected executive.'));
            }
        },
        error: function() {
            frappe.msgprint(__('An error occurred while fetching receipt numbers.'));
        }
    });
}

function updateDialogOptions(response, dialog, selectedReceipts, frm) {
    const uniqueDates = [...new Set(response.message.map(receipt => receipt.date))];
    const uniqueAmounts = [...new Set(response.message.map(receipt => receipt.amount))];

    dialog.set_df_property('date', 'options', uniqueDates);
    dialog.set_value('date', frappe.datetime.nowdate());
    dialog.set_df_property('amount', 'options', uniqueAmounts);

    const receiptOptions = response.message.map(receipt => `
        <tr>
            <td><input type="checkbox" class="receipt-checkbox" value="${receipt.name}"></td>
            <td>${receipt.name}</td>
            <td>${receipt.date}</td>
            <td>${receipt.amount}</td>
        </tr>
    `).join('');

    const tableHTML = `
        <table class="table table-bordered">
            <thead>
                <tr>
                    <th>Select</th>
                    <th>Receipt Number</th>
                    <th>Receipt Date</th>
                    <th>Receipt Amount</th>
                </tr>
            </thead>
            <tbody>${receiptOptions}</tbody>
        </table>`;

    dialog.set_value('receipt_number', tableHTML);
    dialog.$wrapper.find('.receipt-checkbox').off('change').on('change', handleReceiptSelection);

    function handleReceiptSelection() {
        const receiptNumber = $(this).val();
        if ($(this).is(':checked')) {
            selectedReceipts.push(receiptNumber);
            if (dialog.get_value('date')) {
                get_receipt_information(frm, dialog.get_value('executive') || 'All', selectedReceipts.join(', '), dialog.get_value('date'), dialog.get_value('amount'));
                dialog.hide();
            } else {
                frappe.msgprint(__('Please select a date.'));
            }
        } else {
            selectedReceipts = selectedReceipts.filter(num => num !== receiptNumber);
        }
    }
}

function get_receipt_information(frm, executive, selected_receipts, selectedDate, selectedAmt) {
    frappe.call({
        method: 'sil.services.receipt_information_api.getAllReceiptInfoByExecutiveAndReceiptNo',
        args: { executive, receipt_number: selected_receipts, selected_date: selectedDate, selected_amount: selectedAmt },
        callback: function(r) {
            if (r.message && r.message.length) {
                populateFormFields(frm, r.message);
            } else {
                frappe.msgprint(__('No receipt information found for the selected executive, date, and receipt numbers.'));
            }
        },
        error: function() {
            frappe.msgprint(__('An error occurred while fetching receipt information.'));
        }
    });
}

function populateFormFields(frm, receipts) {
    receipts.forEach(receipt => {
        frm.set_value('mode_of_payment', receipt.mode_of_payment || '');
        frm.set_value('executive', receipt.executive || '');
        frm.set_value('bank_account', receipt.bank_account || '');
        frm.set_value('account_paid_to', receipt.account_paid_to || '');
        frm.set_value('date', frappe.datetime.nowdate());
        frm.set_value('amount_received', receipt.amount || 0);
        frm.set_value('reference_number', receipt.reference_number || null);
        frm.set_value('chequereference_date', receipt.chequereference_date || null);
        frm.set_value('account_paid_from', receipt.account_paid_from || null);

        if (receipt.receipt_entries) {
            frm.clear_table('payment_entry_details');
            receipt.receipt_entries.forEach(entry => {
                let child = frm.add_child('payment_entry_details');
                Object.assign(child, {
                    customer: entry.customer || '',
                    reference_type: entry.reference_type || '',
                    reference_name: entry.reference_name || '',
                    outstanding_amount: entry.outstanding_amount || 0,
                    allocated_amount: entry.allocated_amount || 0
                });
            });
            frm.refresh_field('payment_entry_details');
        }
    });
}
