frappe.web_form.after_load = () => {
    // Dynamically get the logged-in user (employee name) and set it to the employee_name field
    var employeeName = frappe.session.user;
    frappe.web_form.set_value('employee_name', employeeName);

    // Add the custom button to show attendance
    var r = $('<input type="button" value="Show Attendance"/>');
    $(".web-form-head .web-form-actions").append(r);

    // Set up the button click event to show the attendance table
    r.on('click', function() {
        // Create a simple static table HTML structure
        var tableHtml = `
            <table class="table table-bordered" style="margin-top: 20px;">
                <thead>
                    <tr>
                        <th>Employee Name</th>
                        <th>Attendance Date</th>
                        <th>Status</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td>John Doe</td>
                        <td>2024-12-17</td>
                        <td>Present</td>
                    </tr>
                    <tr>
                        <td>Jane Smith</td>
                        <td>2024-12-17</td>
                        <td>Absent</td>
                    </tr>
                </tbody>
            </table>
        `;

        // Debugging: Check if the table HTML is created correctly
        console.log(tableHtml);

        // Append the table to the Web Form (below the fields)
        $(".web-form-main").append(tableHtml);

        // Debugging: Check if the table is appended to the Web Form
        console.log('Table appended!');
    });
};
